                else if (${xpath_eval(tokenize('${answer(@date)}', '-')[2])}=05)
                then ('Mai')
                else if (${xpath_eval(tokenize('${answer(@date)}', '-')[2])}=06)
                then ('Juni')
                else if (${xpath_eval(tokenize('${answer(@date)}', '-')[2])}=07)
                then ('Juli')
                else if (${xpath_eval(tokenize('${answer(@date)}', '-')[2])}=08)
                then ('August')
                else if (${xpath_eval(tokenize('${answer(@date)}', '-')[2])}=09)
                then ('September')
                else if (${xpath_eval(tokenize('${answer(@date)}', '-')[2])}=10)
